mapper.select:
SELECT ds.drilling_stress_id AS drillingStressId, ds.measure_num AS measureNum, ds.survey_area_name AS surveyAreaName, ds.workface_id AS workFaceId, ds.sensor_type AS sensorType, ds.sensor_location AS sensorLocation, ds.install_time AS installTime, ds.install_depth AS installDepth, ds.initial_stress AS initialStress, ds.status AS status FROM drilling_stress ds
AND ds.survey_area_name like concat('%',#{measureSelectDTO.surveyAreaName},'%')
AND ds.workface_id = #{measureSelectDTO.workFaceId}
AND ds.status = #{measureSelectDTO.status}
AND ds.install_time > = #{measureSelectDTO.startTime}
AND ds.install_time < = #{measureSelectDTO.endTime}
AND ds.mine_id = #{mineId} AND ds.del_flag = '0'
ORDER BY ds.install_time DESC
