mapper.select:
SELECT ds.drilling_stress_id AS drillingStressId, ds.measure_num AS measureNum, ds.survey_area_id AS surveyAreaId, ds.workface_id AS workFaceId, ds.sensor_type AS sensorType, ds.sensor_location AS sensorLocation, ds.install_time AS installTime, ds.install_depth AS installDepth, ds.initial_stressAS initialStress, ds.status AS status FROM drilling_stress ds
AND ds.survey_area_id = #{surveyAreaId}
AND ds.workface_id = #{workFaceId}
AND ds.status = #{status}
AND ds.install_time > = #{startTime}
AND ds.install_time < = #{endTime}
AND ds.del_flag = '0'
ORDER BY ds.install_time DESC
