mapper.select:
SELECT ld.displacement_id AS displacementId, ld.measure_num AS measureNum, ld.survey_area_name AS surveyAreaName, ld.workface_id AS workFaceId, ld.tunnel_id AS tunnelId, ld.sensor_type AS sensorType, ld.sensor_location AS sensorLocation, ld.install_time AS installTime, ld.status AS status FROM lane_displacement ld
AND ld.survey_area_name like concat('%',#{measureSelectDTO.surveyAreaName},'%')
AND ld.workface_id = #{measureSelectDTO.workFaceId}
AND ld.status = #{measureSelectDTO.status}
AND ld.install_time >= #{measureSelectDTO.startTime}
AND ld.install_time <= #{measureSelectDTO.endTime}
AND ld.mine_id = #{mineId} AND ld.del_flag = '0'
ORDER BY ld.install_time DESC
