mapper.select:
select
p.plan_id as planId,
p.annual as annual,
p.workface_id as workFaceId,
p.plan_type as planType,
p.type as type,
p.drill_type as drillType,
p.plan_name as planName,
p.total_drill_number as totalDrillNumber,
p.total_hole_depth as totalHoleDepth,
p.start_time as startTime,
p.end_time as endTime,
p.state as state,
p.create_by as createBy,
p.dept_id as deptId,
p.del_flag as delFlag
from plan as p


        and p.annual = #{selectNewPlanDTO.annual}


        and p.type = #{selectNewPlanDTO.type}


        and p.drill_type = #{selectNewPlanDTO.drillType}


        and p.state = #{selectNewPlanDTO.state}


        and p.start_time >= #{selectNewPlanDTO.startTime}


        and p.end_time <= #{selectNewPlanDTO.endTime}





                    p.dept_id in

                        #{deptId}


                        or p.create_by = #{userName}




                p.create_by = #{userName}



    and p.state in ('1','2') and p.del_flag = '0'

order by p.start_time desc

select distinct
p.plan_id as planId,
p.annual as annual,
p.workface_id as workFaceId,
p.plan_type as planType,
p.type as type,
p.drill_type as drillType,
p.plan_name as planName,
p.total_drill_number as totalDrillNumber,
p.total_hole_depth as totalHoleDepth,
p.start_time as startTime,
p.end_time as endTime,
p.state as state,
p.create_by as createBy,
p.dept_id as deptId,
p.del_flag as delFlag
from plan as p


        and p.annual = #{selectNewPlanDTO.annual}


        and p.type = #{selectNewPlanDTO.type}


        and p.drill_type = #{selectNewPlanDTO.drillType}


        and p.state = #{selectNewPlanDTO.state}


        and p.start_time >= #{selectNewPlanDTO.startTime}


        and p.end_time <= #{selectNewPlanDTO.endTime}





                    p.dept_id in

                        #{deptId}


                        or p.create_by = #{userName}




                p.create_by = #{userName}



    and p.state in ('3','4') and p.del_flag = '0'

order by p.create_time desc
