mapper.select:
SELECT ra.roof_abscission_id AS roofAbscissionId, ra.measure_num AS measureNum, ra.survey_area_name AS surveyAreaName, ra.workface_id AS workFaceId, ra.tunnel_id AS tunnelId, ra.sensor_type AS sensorType, ra.sensor_location AS sensorLocation, ra.install_time AS installTime, ra.shallow_init_depth AS shallowInitDepth, ra.deep_init_depth AS deepInitDepth, ra.status AS status FROM roof_abscission ra
AND ra.survey_area_name like concat('%',#{measureSelectDTO.surveyAreaName},'%')
AND ra.workface_id = #{measureSelectDTO.workFaceId}
AND ra.status = #{measureSelectDTO.status}
AND ra.install_time > = #{measureSelectDTO.startTime}
AND ra.install_time < = #{measureSelectDTO.endTime}
AND ra.mine_id = #{mineId} AND ra.del_flag = '0'
ORDER BY ra.install_time DESC
