mapper.select:
SELECT sr.support_resistance_id AS supportResistanceId, sr.measure_num AS measureNum, sr.survey_area_name AS surveyAreaName, sr.workface_id AS workFaceId, sr.sensor_type AS sensorType, sr.column_num AS columnNum, sr.column_name AS columnName, sr.sensor_location AS sensorLocation, sr.data_time AS dataTime, sr.status AS status FROM support_resistance sr
AND sr.survey_area_name like concat('%',#{measureSelectDTO.surveyAreaName},'%')
AND sr.workface_id = #{measureSelectDTO.workFaceId}
AND sr.status = #{measureSelectDTO.status}
AND sr.data_time > = #{measureSelectDTO.startTime}
AND sr.data_time < = #{measureSelectDTO.endTime}
AND sr.mine_id = #{mineId} AND sr.del_flag = '0'
ORDER BY sr.data_time DESC
