mapper.select:
SELECT sr.support_resistance_id AS supportResistanceId, sr.measure_num AS measureNum, sr.survey_area_id AS surveyAreaId, sr.work_face_id AS workFaceId, sr.sensor_type AS sensorType, sr.column_num AS columnNum, sr.column_name AS columnName, sr.sensor_location AS sensorLocation, sr.data_time AS dataTime, sr.status AS status FROM support_resistance sr
AND sr.survey_area_id = #{surveyAreaId}
AND sr.work_face_id = #{workFaceId}
AND sr.status = #{status}
AND sr.data_time > = #{startTime}
AND sr.data_time < = #{endTime}
AND sr.del_flag = '0'
ORDER BY sr.data_time DESC
